const wm = this.getMods();
const mkdirp = wm.require('mkdirp');
const db = wm.require('quick.db');
const fs = require('fs');
const dir = `./resources/database/${server.id}/`;
const path = `./resources/database/${server.id}/log_chan.txt`;
const { stripIndents } = wm.require(`common-tags`);
//Get command args
const prefix = (msg.content.startsWith(String(me))) ? String(me) : (server && server.tag) || Files.data.settings.tag;
const args = msg.content.slice(prefix.length).trim().split(/ +/g); args.shift();
const query = args.join(' ');
//define embeds
let error_embed = new DiscordJS.MessageEmbed();
//define regex
const cc = /(channel[-]?created)/gi;
const cd = /(channel[-]?deleted)/gi;
const cu = /(channel[-]?deleted)/gi;
const vcc = /((voice|v)[-]?channel[-]?created)/gi;
const vcd = /((voice|v)[-]?channel[-]?created)/gi;
const mj = /(member[-]?join(ed)?)/gi;
const ml = /(member[-]?leave)/gi;
const me1 = /(message[-]?edited)/gi;
const md = /(message[-]?deleted)/gi;
const rc = /(role[-]?created)/gi;
const rd = /(role[-]?deleted)/gi;
const ru = /(role[-]?updated)/gi;
const ec = /(emoji[-]?created)/gi;
const ed = /(emoji[-]?deleted)/gi;

if (msg.member.permissions.has('MANAGE_GUILD')) {
    if (!fs.existsSync(dir)) {
        mkdirp(dir);
    }
    if (fs.existsSync(path)) {
        error_embed.setTitle(':x: Invalid parameter.');
        error_embed.addField('Valid Parameters', stripIndents(`
        channel-created
        channel-deleted
        channel-updated
        v-channel-created
        v-channel-deleted
        member-join
        member-leave
        message-edited
        message-deleted
        role-created
        role-deleted
        role-updated
        emoji-created
        emoji-deleted
        `), true);
        if (cc.test(query)) {
            if (db.get(`${server.id}log_system.channel-created`) == "enabled") {
                db.set(`${server.id}log_system.channel-created`, 'disabled');
                msg.channel.send('Channel-Created logs has been disabled.');
            } else {
                db.set(`${server.id}log_system.channel-created`, 'enabled');
                msg.channel.send('Channel-Created logs has been enabled.');
            }
        } else if (cd.test(query)) {
            if (db.get(`${server.id}log_system.channel-deleted`) == "enabled") {
                db.set(`${server.id}log_system.channel-deleted`, 'disabled');
                msg.channel.send('Channel-Deleted logs has been disabled');
            } else {
                db.set(`${server.id}log_system.channel-deleted`, 'enabled');
                msg.channel.send('Channel-Deleted logs has been enabled');
            }
        } else if (cu.test(query)) {
            if (db.get(`${server.id}log_system.channel-updated`) == "enabled") {
                db.set(`${server.id}log_system.channel-updated`, 'disabled');
                msg.channel.send('Channel-Updated logs has been disabled');
            } else {
                db.set(`${server.id}log_system.channel-updated`, 'enabled');
                msg.channel.send('Channel-Updated logs has been enabled');
            }
        } else if (vcc.test(query)) {
            if (db.get(`${server.id}log_system.v-channel-created`) == "enabled") {
                db.set(`${server.id}log_system.v-channel-created`, 'disabled');
                msg.channel.send('Voice-Channel-Created logs has been disabled.');
            } else {
                db.set(`${server.id}log_system.v-channel-created`, 'enabled');
                msg.channel.send('Voice-Channel-Created logs has been enabled.');
            }
        } else if (vcd.test(query)) {
            if (db.get(`${server.id}log_system.v-channel-deleted`) == "enabled") {
                db.set(`${server.id}log_system.v-channel-deleted`, 'disabled');
                msg.channel.send('Voice-Channel-Deleted logs has been disabled');
            } else {
                db.set(`${server.id}log_system.v-channel-deleted`, 'enabled');
                msg.channel.send('Voice-Channel-Deleted logs has been enabled');
            }
        } else if (mj.test(query)) {
            if (db.get(`${server.id}log_system.member-join`) == "enabled") {
                db.set(`${server.id}log_system.member-join`, 'disabled');
                msg.channel.send('Member-Join logs has been disabled');
            } else {
                db.set(`${server.id}log_system.member-join`, 'enabled');
                msg.channel.send('Member-Join logs has been enabled');
            }
        } else if (ml.test(query)) {
            if (db.get(`${server.id}log_system.member-leave`) == "enabled") {
                db.set(`${server.id}log_system.member-leave`, 'disabled');
                msg.channel.send('Member-Leave logs has been disabled');
            } else {
                db.set(`${server.id}log_system.member-leave`, 'enabled');
