            }
        } else if (me1.test(query)) {
            if (db.get(`${server.id}log_system.message-edited`) == "enabled") {
                db.set(`${server.id}log_system.message-edited`, 'disabled');
                msg.channel.send('Message-Edited logs has been disabled');
