            if (db.get(`${server.id}log_system.message-deleted`) == "enabled") {
                db.set(`${server.id}log_system.message-deleted`, 'disabled');
                msg.channel.send('Message-Deleted logs has been disabled');
            } else {
                db.set(`${server.id}log_system.message-deleted`, 'enabled');
                msg.channel.send('Message-Deleted logs has been enabled');
            }
        } else if (rc.test(query)) {
            if (db.get(`${server.id}log_system.role-created`) == "enabled") {
                db.set(`${server.id}log_system.role-created`, 'disabled');
                msg.channel.send('Role-Created logs has been disabled');
            } else {
                db.set(`${server.id}log_system.role-created`, 'enabled');
                msg.channel.send('Role-Created logs has been enabled');
            }
        } else if (rd.test(query)) {
            if (db.get(`${server.id}log_system.role-deleted`) == "enabled") {
                db.set(`${server.id}log_system.role-deleted`, 'disabled');
                msg.channel.send('Role-Deleted logs has been disabled');
            } else {
                db.set(`${server.id}log_system.role-deleted`, 'enabled');
                msg.channel.send('Role-Deleted logs has been enabled');
            }
        } else if (ru.test(query)) {
            if (db.get(`${server.id}log_system.role-updated`) == "enabled") {
                db.set(`${server.id}log_system.role-updated`, 'disabled');
                msg.channel.send('Role-Updated logs has been disabled');
            } else {
                db.set(`${server.id}log_system.role-updated`, 'enabled');
                msg.channel.send('Role-Updated logs has been enabled');
            }
        } else if (ec.test(query)) {
            if (db.get(`${server.id}log_system.emoji-created`) == "enabled") {
                db.set(`${server.id}log_system.emoji-created`, 'disabled');
                msg.channel.send('Emoji-Created logs has been disabled');
            } else {
                db.set(`${server.id}log_system.emoji-created`, 'enabled');
                msg.channel.send('Emoji-Created logs has been enabled');
            }
        } else if (ed.test(query)) {
            if (db.get(`${server.id}log_system.emoji-deleted`) == "enabled") {
                db.set(`${server.id}log_system.emoji-deleted`, 'disabled');
                msg.channel.send('Emoji-Deleted logs has been disabled');
            } else {
                db.set(`${server.id}log_system.emoji-deleted`, 'enabled');
                msg.channel.send('Emoji-Deleted logs has been enabled');
            }
        } else {
            msg.channel.send(error_embed);
        }
    } else {
        msg.channel.send(`The log system is not set up! Use \`${prefix}setlogchannel\``);
    }
} else {
    msg.channel.send(':x: You are lacking the **MANAGE_GUILD** permission!');
}
