const wm = this.getMods();
const mkdirp = wm.require('mkdirp');
const db = wm.require('quick.db');
const fs = require('fs');
const dir = `./resources/database/${server.id}/`;
const path = `./resources/database/${server.id}/log_chan.txt`;

(() => {
    if (!msg.member.permissions.has('MANAGE_GUILD')) return msg.channel.send(':x: You are lacking the **MANAGE_GUILD** permission!');

    if (!fs.existsSync(dir)) {
        mkdirp(dir);
    }
    if (!fs.existsSync(path)) return msg.channel.send(`The log system is not set up! Use \`${prefix}setlogchannel\``);

    const all = db.get(`${server.id}log_system.all`) || 'disabled';
    const enable = {
        'channel-created': 'enabled',
        'channel-deleted': 'enabled',
        'channel-updated': 'enabled',
        'member-join': 'enabled',
        'member-leave': 'enabled',
        'message-deleted': 'enabled',
        'message-edited': 'enabled',
        'role-created': 'enabled',
        'role-deleted': 'enabled',
        'role-updated': 'enabled',
        'v-channel-created': 'enabled',
        'v-channel-deleted': 'enabled',
        'emoji-created': 'enabled',
        'emoji-deleted': 'enabled',
        'all': 'enabled'
    };
    const disable = {
        'channel-created': 'disabled',
        'channel-deleted': 'disabled',
        'channel-updated': 'disabled',
        'member-join': 'disabled',
        'member-leave': 'disabled',
        'message-deleted': 'disabled',
        'message-edited': 'disabled',
        'role-created': 'disabled',
        'role-deleted': 'disabled',
        'role-updated': 'disabled',
        'v-channel-created': 'disabled',
        'v-channel-deleted': 'disabled',
        'emoji-created': 'disabled',
        'emoji-deleted': 'disabled',
        'all': 'disabled'
    };

    if (all === "enabled") {
        db.set(`${server.id}log_system`, disable);
        msg.channel.send('Everything has been disabled.')
    } else if (all === "disabled") {
        db.set(`${server.id}log_system`, enable);
        msg.channel.send('Everything has been enabled.')
    } else {
        msg.channel.send('An error occured, this is probably due to the log system not being setup in this server.');
    }
})();
