const wm = this.getWrexMods();
const fs = wm.require("fs");
const nodegyp = wm.require('node-gyp');
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const channelcreated = tempVars('channelcreated');
let embed = new DiscordJS.RichEmbed();

if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.channel-created`);
    if (log_system === "enabled") {
        if (!channelcreated.name.startsWith("ticket-")) {
            embed.setTitle('Channel Created');
            embed.setColor('#20fc3a');
            embed.addField('Name', channelcreated, true);
            embed.addField('Category', (channelcreated.parent && channelcreated.parent.name) ? channelcreated.parent.name : "None", true);
            embed.setFooter(`ID: ${channelcreated.id}`);
            embed.setTimestamp();
            server.channels.get(log_chan).send(embed);
            db.add(`${server.id}logs.channel_created`, 1);
            db.add(`${server.id}logs.all`, 1);
        }
    }
}
