const wm = this.getMods();
const fs = require("fs");
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const memberleave = tempVars('memberleave');
let embed = new DiscordJS.MessageEmbed();

(async () => {
if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.member-leave`);
    if (log_system === "enabled") {
        const memS = await server.members.fetch();
        embed.setTitle('Member Left');
        embed.setColor('#3dd0f4');
        embed.setAuthor(memberleave.user.tag, memberleave.user.displayAvatarURL());
        embed.setThumbnail(memberleave.user.displayAvatarURL());
        embed.addField('User', memberleave, true);
        embed.addField('Member Count', server.members.cache.size, true);
        embed.setFooter(`ID: ${memberleave.user.id}`);
        embed.setTimestamp();
        server.channels.cache.get(log_chan)
            .send(embed);
        db.add(`${server.id}logs.member_leave`, 1);
        db.add(`${server.id}logs.all`, 1);
    }
}
})();
