const wm = this.getMods();
const fs = require("fs");
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const channeldeleted = tempVars('channeldeleted');
let embed = new DiscordJS.MessageEmbed();

if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.v-channel-deleted`);
    if (log_system === "enabled") {
        embed.setTitle('Voice Channel Deleted');
        embed.setColor('RED');
        embed.addField('Name', channeldeleted.name, true);
        embed.addField('Category', (channeldeleted.parent && channeldeleted.parent.name) ? channeldeleted.parent.name : "None", true);
        embed.addField('User Limit', channeldeleted.userLimit, true);
        embed.setFooter(`ID: ${channeldeleted.id}`);
        embed.setTimestamp();
        server.channels.cache.get(log_chan).send(embed);
        db.add(`${server.id}logs.v_channel_deleted`, 1);
        db.add(`${server.id}logs.all`, 1);
    }
}
