const wm = this.getWrexMods();
const fs = wm.require("fs");
const mkdirp = wm.require('mkdirp');
const nodegyp = wm.require('node-gyp');
const db = wm.require('quick.db');
const dir = `./resources/database/${server.id}/`;
const path = `./resources/database/${server.id}/log_chan.txt`;
let embed = new DiscordJS.RichEmbed();

const log_system = {
    'channel-created': 'enabled',
    'channel-deleted': 'enabled',
    'channel-updated': 'enabled',
    'member-join': 'enabled',
    'member-leave': 'enabled',
    'message-deleted': 'enabled',
    'message-edited': 'enabled',
    'role-created': 'enabled',
    'role-deleted': 'enabled',
    'role-updated': 'enabled',
    'v-channel-created': 'enabled',
    'v-channel-deleted': 'enabled',
    'emoji-created': 'enabled',
    'emoji-deleted': 'enabled',
    all: 'enabled'
};

msg.delete();

if (msg.member.permissions.has('MANAGE_GUILD')) {
    if (msg.mentions.channels.first()) {
        const mentionC = msg.mentions.channels.first();
        if (!fs.existsSync(dir)) {
            mkdirp(dir);
        }
        if (fs.existsSync(path)) {
            fs.writeFileSync(path, mentionC.id, function (err) {
                if (err) console.log(`Something went wrong while writing: [${err}]`);
            });
            embed.setTitle('Sucessfully Changed');
            embed.setColor('GREEN');
            embed.setThumbnail(`https://cdn.discordapp.com/emojis/482184108555108358.png`);
            embed.setDescription(`Everything related to logs will be posted in ${mentionC} from now on.`);
            embed.setTimestamp();
            msg.channel.send(embed);
        } else {
            fs.writeFileSync(path, mentionC, function (err) {
                if (err) console.log(`Something went wrong while writing: [${err}]`);
            });
            db.set(`${server.id}log_system`, log_system);
            embed.setTitle('Sucessfully Set');
            embed.setColor('GREEN');
            embed.setThumbnail(`https://cdn.discordapp.com/emojis/482184108555108358.png`);
            embed.setDescription(`Everything related to logs will be posted in ${mentionC}.`);
            embed.setTimestamp();
            msg.channel.send(embed);
        }
    } else {
        msg.channel.send(':x: You need to mention a channel!');
    }
} else {
    msg.channel.send(':x: You are lacking the **MANAGE_GUILD** permission!');
}
