const wm = this.getWrexMods();
const fs = wm.require("fs");
const nodegyp = wm.require('node-gyp');
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const channelbefore = tempVars('channelbefore');
const channelafter = tempVars('channelafter');
let embed = new DiscordJS.RichEmbed();

if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.channel-updated`);
    if (log_system === "enabled") {
        if (!channelbefore.name.startsWith("ticket-")) {
            if (channelbefore != channelafter) {
                embed.setTitle(`Channl ${channelbefore.name} Updated`);
                embed.setColor('#20fc3a');
                embed.addField('Name', (channelbefore.name == channelafter.name) ? "Updated: ❌" : `Updated: ✅ \n New Name: ${channelafter.name}`, true);
                embed.addField('Topic', (channelbefore.topic == channelafter.topic) ? "Updated: ❌" : `Updated: ✅ \n New Topic: ${channelafter.topic}`, true);
                embed.addField('Is NSFW?', (channelafter.nsfw) ? "✅" : "❌", true);
                embed.addField('Category', (channelafter.parent && channelbefore.parent.name == channelafter.parent.name) ? "Updated: ❌" : `Updated: ✅ \n New Category: ${channelafter.parent.name}`, true);
                embed.addField('Position', (channelbefore.position == channelafter.position) ? "Updated: ❌" : `Updated:  ✅ \n New Position: ${channelafter.position}`, true);
                embed.setFooter(`ID: ${channelafter.id}`);
                embed.setTimestamp();
                server.channels.get(log_chan).send(embed);
                db.add('logs.channel_updated', 1);
                db.add('logs.all', 1);
            }
        }
    }
}
