const wm = this.getWrexMods();
const fs = wm.require("fs");
const nodegyp = wm.require('node-gyp');
const db = wm.require('quick.db');
const path = `./resources/database/${server.id}/log_chan.txt`;
const rolecreated = tempVars('rolecreated');
let embed = new DiscordJS.RichEmbed();

if (fs.existsSync(path)) {
    const log_chan = fs.readFileSync(path, 'utf8');
    const log_system = db.get(`${server.id}log_system.role-created`);
    if (log_system === "enabled") {
            embed.setTitle('Role Created');
            embed.setColor('#20fc3a');
            embed.addField('Name', rolecreated, true);
            embed.addField('Managed', rolecreated.managed, true);
            embed.addField('Position', rolecreated.position, true);
            embed.setFooter(`ID: ${rolecreated.id}`);
            embed.setTimestamp();
            server.channels.get(log_chan).send(embed);
            db.add(`${server.id}logs.role_created`, 1);
            db.add(`${server.id}logs.all`, 1);
    }
}
