const moment = this.getWrexMods()
    .require('moment');

const prefix = (server && server.tag) || this.getDBM()
    .Files.data.settings.tag;
const text = msg.content.slice(prefix.length)
    .trim()
    .split(/ +/g);
text.shift();
let infoMem;

if (!text) {
    infoMem = msg.member;
} else {
    infoMem = msg.mentions.members.first() || server.members.find(m => m.id === `${text.join(' ')}`) || server.members.find(m => m.displayName.toUpperCase() === `${text.join(' ').toUpperCase()}`) || server.members.find(m => m.user.username.toUpperCase() === `${text.join(' ').toUpperCase()}`) || server.members.find(m => m.user.username.toLowerCase().includes(`${text.join(' ').toLowerCase()}`));
}

(async () => {
    if (!infoMem) {
        try {
            infoMem = await client.fetchUser(text[0]);
        } catch (err) {
            infoMem = msg.member;
        }
    }

    if (server.member(infoMem)) {
        const joinPosition = getJoinRank(infoMem.id, msg.guild);
        const ts = moment(infoMem.createdAt);
        const ts2 = moment(infoMem.joinedAt);
        const ca = ts.format("MMM Do, YYYY");
        const ja = ts2.format("MMM Do, YYYY");
        const roles = infoMem.roles.sort((a, b) => b.position - a.position);
        let roles1 = roles.filter(r => r.id !== msg.guild.id)
            .array()
            .join(', ');
        if (roles1 === undefined || roles1.length == 0) {
            roles1 = "No Roles";
        }
        const embed = new DiscordJS.RichEmbed()
            .setTitle(`${infoMem.user.username}'s Info`)
            .setColor('RANDOM')
            .setThumbnail(infoMem.user.displayAvatarURL)
            .setAuthor(msg.member.displayName, msg.author.displayAvatarURL)
            .addField('**User Tag**', infoMem.user.tag, true)
            .addField('**Nickname**', infoMem.displayName, true)
            .addField('**User ID**', infoMem.id, true)
            .addField('**Status**', infoMem.user.presence.status, true)
            .addField('**Joined Server**', ja, true)
            .addField('**Account Created**', ca, true)
            .addField('**Join Position**', `${Number(joinPosition) + 1}`, true)
            .addField('**Roles**', roles1, false);
        if (me.permissions.has('MANAGE_MESSAGES')) msg.delete();
        msg.channel.send(embed);
    } else {
        //not guild member
        const ts = moment(infoMem.createdAt);
        const ca = ts.format("MMM Do, YYYY");
        const embed = new DiscordJS.RichEmbed()
            .setTitle(`${infoMem.username}'s Info`)
            .setColor("RANDOM")
            .setThumbnail(infoMem.displayAvatarURL)
            .setAuthor(msg.member.displayName, msg.author.displayAvatarURL)
            .addField('**User Tag**', infoMem.tag)
            .addField('**User ID**', infoMem.id)
            .addField('**Is Bot?**', infoMem.bot)
            .addField('**Account Created**', ca);
        if (me.permissions.has('MANAGE_MESSAGES')) msg.delete();
        msg.channel.send(embed);
    }

    function getJoinRank(ID, guild) { // Call it with the ID of the user and the guild
        if (!guild.member(ID)) return; // It will return undefined if the ID is not valid

        let arr = guild.members.array(); // Create an array with every member
        arr.sort((a, b) => a.joinedAt - b.joinedAt); // Sort them by join date

        for (let index = 0; index < arr.length; index++) { // Loop though every element
            if (arr[index].id == ID) return index; // When you find the user, return it's position
        }
    }
})();
