const city = tempVars("city");
const weather = this.getWrexMods()
    .require('weather-js');
const {
    RichEmbed
} = require('discord.js');
//Created by TheMonDon :3

weather.find({
    search: city,
    degreeType: 'F'
}, function(err, result) {
    if (err) console.log(err);

    if (result.length === 0) {
        msg.channel.send(`No data was available for the location \`${(String(city).length > 1959) ? String(city).substring(0,1956)+'...' : city}\``);
    } else {

        const dc = Math.round((result[0].current.temperature - 32) * 5 / 9);
        const dc2 = Math.round((result[0].current.feelslike - 32) * 5 / 9);
        const b4 = result[0].current.winddisplay.split('mph');
        const a4 = Math.round(b4[0] * 1.609344) + ' kph' + b4[1];

        const embed = new RichEmbed()
            .setColor('RANDOM')
            .setTitle(`Weather in: ${result[0].location.name}`)
            .setThumbnail(result[0].current.imageUrl)
            .addField('Tempature: ', `${result[0].current.temperature}째F \n ${dc}째C`, true)
            .addField('Feels Like: ', `${result[0].current.feelslike}째F \n ${dc2}째C`, true)
            .addField('Humidity: ', `${result[0].current.humidity}%`, true)
            .setDescription(`**Sky weather:** ${result[0].current.skytext} \n\n **Wind info:** ${result[0].current.winddisplay} (${a4})`);
        msg.channel.send(embed);
    }
});
