const city = tempVars("city");
const weather = this.getWrexMods().require('weather-js');
const { RichEmbed } = require('discord.js');
//Created by TheMonDon :3

weather.find({search: city, degreeType: 'F'}, function(err, result) {
    if(err) console.log(err);

    if (result.length === 0) {
        msg.channel.send(`No data was available for the location \`${city}\``);
    } else {

    const embed = new RichEmbed()
        .setColor('RANDOM')
        .setTitle(`Weather in: ${result[0].location.name}`)
        .setThumbnail(result[0].current.imageUrl)
        .addField('Tempature: ', `${result[0].current.temperature}°F`, true)
        .addField('Feels Like: ', `${result[0].current.feelslike}°F`, true)
        .addField('Humidity: ', `${result[0].current.humidity}%`, true)
        .setDescription(`**Sky weather:** ${result[0].current.skytext} \n\n **Wind info:** ${result[0].current.winddisplay}`);
    msg.channel.send(embed);
}
});
